import React, { Component } from 'react';
import './App.css'
import Light from './Light'
import User from './User'

class App extends Component {
  constructor() {
    super()
    this.state = {
      lightBulbArray: []
    }
  }

  addLightBulb = () => {
    console.log("test");
    let { lightBulbArray } = this.state
    lightBulbArray.push(<Light />)
    this.setState({ lightBulbArray: lightBulbArray })
  }

  render() {
    let { lightBulbArray } = this.state

    return (
        <div>
          <button onClick={ this.addLightBulb }> Add bulb </button>
          { lightBulbArray }
        </div>
    );
  }
}

export default App;
